import { i18n, uuiDayjs } from '@epam/uui-components';

// Reset datePicker configuration
i18n.datePicker = {};

// Set German locale
uuiDayjs.dayjs.locale('de');

// Optionally override first day of week (0=Sunday, 1=Monday, 6=Saturday)
uuiDayjs.dayjs.updateLocale('de', { weekStart: 1 }); // Monday first
